import React, { useContext } from 'react'
import { Link, useLocation } from 'react-router-dom'
import '../css/Navbar.css'
import DataContext from '../Context/Context'

const Navbar = () => {
  const {loadingEffect} = useContext(DataContext)
  let currentPage = useLocation()
  currentPage = currentPage.pathname.substring(1);
  currentPage = currentPage.charAt(0).toUpperCase() + currentPage.slice(1)
  //console.log(currentPage)
  return (
    <nav className='Nav'>
      <p className='logo'><Link to='/'>Democracy Roleplay</Link></p>
      <div>
        <ul id='ulNavbar'>
          <li className='active'><Link to='/' onClick={loadingEffect} style={currentPage === 'Home' ? {color:'#3ca918'} : null}>Home</Link></li>
          {/* <li className='active'><Link to='/Apply' style={currentPage === 'Apply' ? {color:'#3ca918'} : null}>Apply Here</Link></li> */}
          <li><Link to={{ pathname: `$https://docs.google.com/forms/d/e/1FAIpQLScqljWWwP5pUgwWPbNpiuTBd6w6OOF17fYh5Cc_020ZL1swTw/viewform?usp=sf_link` }} target="_blank" >Apply Here</Link></li>
          <li className='active'><Link to='/Rules' style={currentPage === 'Rules' ? {color:'#3ca918'} : null}>Rules</Link></li>
          <li className='active'><Link to='/ContactUs' style={currentPage === 'ContactUs' ? {color:'#3ca918'} : null}>Contact Us</Link></li>
        </ul>
       </div>
    </nav>
  )
}

export default Navbar
