import { createContext, React, useState, useEffect } from 'react'
import apiRequests from '../api/apiRequests'
import Popup from 'reactjs-popup';

const DataContext = createContext({})

export const Context = ({children}) => {
    const [isLoading, setIsLoading] = useState(true)
    const [errors, setErrors] = useState({});
    useEffect(() => {
        setTimeout(() => {
            setIsLoading(false)
        }, 3000)
    }, [])

    //Apply form
    const [ formData, setFormData ] = useState({
        name:'',
        email:'',
        age:'',
        discordTag:'',
        ytLink:'',
        rpExp:'',
        charBackStory:'',
        isRulesAgreed:false
    })
    const handleFormChange = (e) => {
        const { id, value } = e.target
        console.log(e.target)
        let error = ''
        if(id === 'name' && value.length < 4)
            error = 'Invalid Name'
        else if(id === 'email' && value.length < 10)
            error = 'Invalid Email Address'
        else if(id === 'age' && (value < 0 || value > 100 ))
            error = 'Invalid Age'
        else if(id === 'discordTag' && value.length <= 0)
            error = 'Invalid Discord Tag'
        else if(id === 'rpExp' && (value < 0 && value > 7))
            error = 'Invalid Experience'
        else if(id === 'charBackStory')
        {
            let wordCount = value.split(' ')
            if(wordCount.length < 100)
                error = 'Backstory should  contains atleast 100 words'
        }
        setErrors({
            ...errors,
            [name]: error
        });
        setFormData({
            ...formData,
            [name]: value
        });
    }
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [age, setAge] = useState('')
    const [discordTag, setDiscordTag] = useState('')
    const [rpExp, setRpExp] = useState('')
    const [charBackStory, setCharBackStory] = useState('')
    const [ytLink, setYtLink] = useState('')
    const [isRulesAgreed, setIsRulesAgreed] = useState(false)
    const [hideCharInfo, showCharInfo] = useState(false)
    const submitForm = (e) => {
        e.preventDefaults()
    }
    const setAgreed = () => {
        setFormData( ...formData, formData.isRulesAgreed=!formData.isRulesAgreed)
    }
    return (
        <DataContext.Provider value={{
            isLoading, setIsLoading, name, setName, 
            email, setEmail, age, setAge, discordTag, 
            setDiscordTag, rpExp, setRpExp, charBackStory, 
            setCharBackStory, ytLink, setYtLink, isRulesAgreed, 
            setIsRulesAgreed, submitForm, setAgreed,
            hideCharInfo, showCharInfo,
            handleFormChange, formData, errors
        }}>
            {children}
        </DataContext.Provider>
    )
}

export default DataContext
