import { createContext, React, useState, useEffect } from 'react'
import apiRequests from '../api/apiRequests'
import Popup from 'reactjs-popup';

const DataContext = createContext({})

export const Context = ({children}) => {
    const [isLoading, setIsLoading] = useState(true)
    const alert = useAlert()
    useEffect(() => {
        setTimeout(() => {
            setIsLoading(false)
        }, 3000)
    }, [])
    const loadingEffect = () => {
        setTimeout(() => {
            setIsLoading(!isLoading)
        }, 2000)
    }
    //Apply form
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [age, setAge] = useState('')
    const [discordTag, setDiscordTag] = useState('')
    const [rpExp, setRpExp] = useState('')
    const [charBackStory, setCharBackStory] = useState('')
    const [ytLink, setYtLink] = useState('')
    const [isRulesAgreed, setIsRulesAgreed] = useState(false)
    const submitForm = (e) => {
        e.preventDefaults()
        const wordCount = charBackStory.split(' ').length;
        if (wordCount < 100) 
        {
            alert('Character Backstory must be at least 100 words');
        }
    }
    const setAgreed = () => {
        setIsRulesAgreed(!isRulesAgreed)
        console.log(isRulesAgreed)
    }
    return (
        <DataContext.Provider value={{
            isLoading, setIsLoading, name, setName, 
            email, setEmail, age, setAge, discordTag, 
            setDiscordTag, rpExp, setRpExp, charBackStory, 
            setCharBackStory, ytLink, setYtLink, isRulesAgreed, 
            setIsRulesAgreed, submitForm, setAgreed, loadingEffect
        }}>
            {children}
        </DataContext.Provider>
    )
}

export default DataContext
