  console.log(pageName.charAt(0).toUpperCase() + pageName.slice(1));
  return (
    <nav className='Nav'>
      <p className='logo'><Link to='/'>Democracy Roleplay</Link></p>
      <div>
        <ul id='ulNavbar'>
          <li className='active' style={currentPage == '' ? {color:'#3ca918'} : null}><Link to='/'>Home</Link></li>
          <li className='active'><Link to='/Apply'>Apply Here</Link></li>
          <li className='active'><Link to='/Rules'>Rules</Link></li>
          <li className='active'><Link to='/ContactUs'>Contact Us</Link></li>
        </ul>
       </div>
    </nav>
  )
}

export default Navbar
