import React, { useEffect } from 'react'
import { useContext } from 'react'
import DataContext from '../Context/Context'
import './Apply.css'
import Popup from 'reactjs-popup';
import { PersonalInfo } from '../Components/PersonalInfo';
import CharacterInfo from '../Components/CharacterInfo';


const Apply = () => {
  const {name, setName, age, setAge, 
    email, setEmail, discordTag, setDiscordTag, 
    rpExp, setRpExp, ytLink, setYtLink, charBackStory,
    setCharBackStory, isRulesAgreed, setIsRulesAgreed, submitForm, setAgreed, loadingEffect, isLoading, hideCharInfo} = useContext(DataContext)
    useEffect(() => {
      showCharInfo(!hideCharInfo)
    }, [hideCharInfo])
    return (
    <div className='applyDiv'>
      <div className='overlay'></div>
      { hideCharInfo 
        ? 
          <PersonalInfo />
        :
          <CharacterInfo  />
      }
    </div>
  )
}

export default Apply
