import React, { useEffect } from 'react'
import { useContext } from 'react'
import DataContext from '../Context/Context'
import './Apply.css'
import Popup from 'reactjs-popup';
import { PersonalInfo } from '../Components/PersonalInfo';
import CharacterInfo from '../Components/CharacterInfo';


const Apply = () => {
  const { errors, hideCharInfo, showCharInfo } = useContext(DataContext)
    useEffect(() => {
      showCharInfo(false)
    }, [showCharInfo])
    console.log("Error Card:", errors)
    return (
    <div className='applyDiv'>
      <div className='overlay'></div>
      {errors.name.length === 0 ? <div className='errorCard'>
        {errors.name && <div className='errors'>{errors.name}</div>}
        {errors.age && <div className='errors'>{errors.age}</div>}
        {errors.email && <div className='errors'>{errors.email}</div>}
        {errors.discordTag && <div className='errors'>{errors.discordTag}</div>}
        {errors.rpExp && <div className='errors'>{errors.rpExp}</div>}
        {errors.charName && <div className='errors'>{errors.charName}</div>}
        {errors.charBackStory && <div className='errors'>{"Character Backstory must be " + errors.charBackStory}</div>}
        {errors.general && <div className='errors'>{errors.general}</div>}
      </div> : null}
      { !hideCharInfo 
        ? 
          <PersonalInfo />
        :
          <CharacterInfo  />
      }
    </div>
  )
}

export default Apply
